#include "main.h"
#include "gtest/gtest.h"
#include <stdexcept>
using namespace std;

TEST(AOC16Test, WebTest1) {
    string input = "D2FE28";
    EXPECT_EQ(6, sumOfVers(input));
}

TEST(AOC16Test, WebTest2) {
    string input = "38006F45291200";
    EXPECT_EQ(9, sumOfVers(input));
}

TEST(AOC16Test, WebTest3) {
    string input = "EE00D40C823060";
    EXPECT_EQ(14, sumOfVers(input));
}

TEST(AOC16Test, WebTest4) {
    string input = "8A004A801A8002F478";
    EXPECT_EQ(16, sumOfVers(input));
}

TEST(AOC16Test, WebTest5) {
    string input = "620080001611562C8802118E34";
    EXPECT_EQ(12, sumOfVers(input));
}

TEST(AOC16Test, WebTest6) {
    string input = "C0015000016115A2E0802F182340";
    EXPECT_EQ(23, sumOfVers(input));
}

TEST(AOC16Test, WebTest7) {
    string input = "A0016C880162017C3686B18A3D4780";
    EXPECT_EQ(31, sumOfVers(input));
}

TEST(AOC16Test, SubmisionTest) {
    string input = "";
    EXPECT_EQ(981, sumOfVers(input));
}

TEST(AOC16Test, PacketShort) {
    string input = "A";
    EXPECT_THROW(sumOfVers(input), invalid_argument);
}

TEST(AOC16Test, LiteralPayloadShort) {
    string input = "02";
    EXPECT_THROW(sumOfVers(input), out_of_range);
}

TEST(AOC16Test, InvalidHex) {
    string input = "M2";
    EXPECT_THROW(sumOfVers(input), invalid_argument);
}

int main() {
    testing::InitGoogleTest();
    RUN_ALL_TESTS();
}